	var testUtil = new NeedItUtils();
	var isPast = testUtil.isDatePast(current.u_when_needed);
	if (isPast){
		gs.addErrorMessage("La data della richiesta non può essere nel passato. La tua richiesta non è stata salvata sul database");
		current.setAbortAction(true);
	}

	var isToday = testUtil.isDateToday(current.u_when_needed);
	if (isToday){
		gs.addErrorMessage("Richieste per lo stesso giorno non sono consentite. La tua richiesta non è stata salvata sul database.");
		current.setAbortAction(true);
	}

})(current, previous);
